import React from 'react';
import './App.css';
import ZingTouch from 'zingtouch';
import Menu from './components/Menu';
import Music from './components/music';
import Song from './components/song';
import Game from './components/game';
import Setting from './components/setting';
import CoverFlow from './components/coverflow';

class App extends React.Component {
    constructor(){
        super();
        this.state={
            showMenu:true,
            showMusic:false,
            showSong:false,
            showGame:false,
            showSetting:false,
            showCoverflow:false,
            showCom: -1
        }
    }

    handleRotation=(e)=>{
        const context=this;
        var distance=0;
        var region=new ZingTouch.Region(e.target)
        region.bind(e.target,'rotate',function(e){
            distance=e.detail.distanceFromOrigin;

             // initilly cardflow state is true
             if(distance>0 && distance<=85) 
             {
                 context.setState({
                        showCom:1

                 })

             }else if(distance>85 && distance<=180){
                 context.setState({
                     showCom:2
                 })
             }else if(distance>180 && distance<=265){
                context.setState({
                    showCom:3
                })
             }else if(distance>265 && distance<350){
                context.setState({
                    showCom:4
                })
             }

        })
    }

    handleClick = (e)=>{
        console.log("clicked");
        console.log(this);
        if(this.state.showCom ===1){
            this.setState({
                showMenu:false,
                showMusic:true,
                showSong:false,
                showGame:false,
                showSetting:false,
                showCoverflow:false,
            })
        }else if(this.state.showCom===2){
            this.setState({
                showMenu:false,
                showMusic:false,
                showSong:false,
                showGame:false,
                showSetting:false,
                showCoverflow:true,
            })
        }else if(this.state.showCom===3){
            this.setState({
                showMenu:false,
                showMusic:false,
                showSong:false,
                showGame:false,
                showSetting:true,
                showCoverflow:false,
            })
        }else if(this.state.showCom===4){
            this.setState({
                showMenu:false,
                showMusic:false,
                showSong:false,
                showGame:true,
                showSetting:false,
                showCoverflow:false,
            })
        }
    }

    handleMenu=(e)=>{
        if(this.state.showSong){
            this.setState({
                    showMenu:false,
                    showMusic:true,
                    showSong:false,
                    showGame:false,
                    showSetting:false,
                    showCoverflow:false,
            })
        }
    }

  render(){
    let showComponent;
    if(this.state.showCoverflow){
        showComponent = <CoverFlow />;
    }else if(this.state.showGame){
        showComponent = <Game/>;
    }else if(this.state.showMenu){
        showComponent = <Menu item={this.state.showCom}  />;
    }else if(this.state.showMusic){
        showComponent = <Music/>;
    }else if(this.state.showSetting){
        showComponent = <Setting/>;
    }else if(this.state.showSong){
        showComponent = <Song/>;
    }
    return(
    <div className="ipodDiv">
        <div className="screen">
           {showComponent}
        </div>
        <div className="bottons_div">
            <div className="btns" onMouseEnter={this.handleRotation} >
                <div className="menu_btn" onClick={this.handleMenu}>
                    MENU
                </div>
                <div className="right">
                <i className="fas fa-fast-forward"></i>
                </div>
                <div className="left">
                <i className="fas fa-fast-backward"></i>
                </div>
                <div className="bottom">
                <i className="fas fa-pause"></i>
                <i className="fas fa-play"></i>
                </div>
                <div className="solid_circle" onClick={this.handleClick} >

                </div>
            </div>
        </div>
    </div>
  )};
}

export default App;
